import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [omikujiData, setOmikujiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-last-data');
        setOmikujiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);

    return () => clearInterval(interval);
  }, []);

  if (!omikujiData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="omikuji-container">
      <h1 className="omikuji-title">おみくじ堂</h1>
      <div className="omikuji-paper">
        <div className="omikuji-content">
          <div className="omikuji-result">{omikujiData.運勢}</div>
          <div className="omikuji-fortune">{omikujiData.助言}</div>
          {Object.entries(omikujiData).map(([key, value]) => (
            key !== '運勢' && key !== '助言' && (
              <div key={key} className="category">
                <span className="category-name">{key}：</span>
                <span className="category-fortune">{value}</span>
              </div>
            )
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
