import React, { useState, useEffect, useRef , useCallback} from 'react';
import axios from 'axios';
import html2canvas from 'html2canvas';
import QRCode from 'qrcode.react';
import './App.css';
import ErrorBoundary from './ErrorBoundary';
// import QRCodeWrapper from './QRCodeWrapper';

function App() {
  const [omikujiData, setOmikujiData] = useState(null);
  const [qrCodeData, setQrCodeData] = useState('');
  const omikujiRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-last-data');
        if (isMounted) {
          setOmikujiData(response.data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);

    return () => {
      isMounted = false;
      clearInterval(interval);
    };
  }, []);

  const [isQRReady, setIsQRReady] = useState(false);

  const captureAndGenerateQR = useCallback(async () => {
    if (omikujiRef.current) {
      try {
        const canvas = await html2canvas(omikujiRef.current);
        const imageData = canvas.toDataURL('image/png');
        setQrCodeData(imageData);
        setIsQRReady(true);
      } catch (error) {
        console.error('Error generating QR code:', error);
      }
    }
  }, []);

  useEffect(() => {
    if (omikujiData) {
      captureAndGenerateQR();
    }
  }, [omikujiData, captureAndGenerateQR]);
  useEffect(() => {
    return () => {
      // コンポーネントのアンマウント時にクリーンアップを行う
      setOmikujiData(null);
      setQrCodeData('');
    };
  }, []);

  if (!omikujiData) {
    return <div>Loading...</div>;
  }

  const firstColumnItems = ['運勢', '助言', '願事', '恋愛', '待人'];
  const secondColumnItems = ['商売', '旅立', '学問', '病気', '開運物', '開運色'];

  return (
    <div className="omikuji-container">
      <div className="omikuji-container">
        <h1 className="omikuji-title">おみくじ堂</h1>
        <div ref={omikujiRef} className="omikuji-paper">
          <div className="omikuji-content">
            <div className="omikuji-column">
              {firstColumnItems.map(key => (
                <div key={key} className={`category ${key === '運勢' ? 'omikuji-result' : ''}`}>
                  <span className="category-name">{key}：</span>
                  <span className="category-fortune">{omikujiData[key]}</span>
                </div>
              ))}
            </div>
            <div className="omikuji-column">
              {secondColumnItems.map(key => (
                <div key={key} className="category">
                  <span className="category-name">{key}：</span>
                  <span className="category-fortune">{omikujiData[key]}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
      {isQRReady && qrCodeData && (
        <div className="qr-code-container">
          <QRCode value={qrCodeData} size={128} />
        </div>
      )}

      <ErrorBoundary>
        {qrCodeData && (
          <div className="qr-code-container">
            <QRCode value={qrCodeData} size={128} />
          </div>
        )}
      </ErrorBoundary >
    </div>
  );
}

export default App;
