import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [omikujiData, setOmikujiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/get-last-data');
        setOmikujiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // 5秒ごとに更新

    return () => clearInterval(interval);
  }, []);

  if (!omikujiData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="omikuji-container">
      <h1>おみくじ堂</h1>
      <div className="omikuji-paper">
        <div className="omikuji-number">第{omikujiData.number}番</div>
        <div className="omikuji-result">{omikujiData.result}</div>
        <div className="omikuji-fortune">{omikujiData.fortune}</div>
        <div className="omikuji-categories">
          {Object.entries(omikujiData.categories).map(([category, fortune]) => (
            <div key={category} className="category">
              <span className="category-name">{category}:</span>
              <span className="category-fortune">{fortune}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
